// Parse the reading response
const reading = $input.item.json;

// Format the date
const date = new Date(reading.date);
const formattedDate = date.toLocaleDateString('en-US', { 
  weekday: 'long', 
  year: 'numeric', 
  month: 'long', 
  day: 'numeric' 
});

// Build card HTML
let cardsHTML = '';
reading.cards.forEach((card, index) => {
  const orientation = card.isReversed ? '‚ü≤ Inverted' : 'Upright';
  const orientationColor = card.isReversed ? '#5eead4' : '#d4af37';
  const meaning = card.isReversed ? card.reversed_meaning : card.upright_meaning;

  // Card image HTML - centered and sized appropriately, with greyscale for inverted
  const cardImageHTML = card.image_url ? `
    <div style="text-align: center; margin-bottom: 20px;">
      <img src="${card.image_url}" alt="${card.name}" style="max-width: 250px; height: auto; border-radius: 8px; box-shadow: 0 4px 12px rgba(212, 175, 55, 0.3); ${card.isReversed ? 'transform: rotate(180deg); filter: grayscale(100%);' : ''}" />
    </div>
  ` : '';

  cardsHTML += `
    <div style="margin-bottom: 30px; padding: 20px; background: linear-gradient(135deg, #1c1917 0%, #292524 100%); border: 2px solid #d4af37; border-radius: 8px;">
      <table width="100%" cellpadding="0" cellspacing="0" style="margin-bottom: 15px;">
        <tr>
          <td style="text-align: left;">
            <h3 style="font-family: 'Georgia', serif; color: #d4af37; margin: 0; font-size: 20px;">${card.position}</h3>
          </td>
          <td style="text-align: right;">
            <span style="font-family: 'Georgia', serif; color: ${orientationColor}; font-size: 14px; font-weight: bold;">${orientation}</span>
          </td>
        </tr>
      </table>
      <h2 style="font-family: 'Georgia', serif; color: #fef3c7; margin: 0 0 12px 0; font-size: 26px; text-align: center;">${card.name}</h2>
      ${cardImageHTML}
      <div style="margin-bottom: 12px; text-align: center;">
        ${card.keywords.map(kw => `<span style="display: inline-block; padding: 4px 12px; margin: 2px 4px 2px 0; background: rgba(212, 175, 55, 0.2); border: 1px solid #d4af37; color: #fbbf24; font-size: 13px; border-radius: 4px; font-family: 'Georgia', serif;">${kw}</span>`).join('')}
      </div>
      <p style="font-family: 'Georgia', serif; color: #e7e5e4; line-height: 1.7; margin: 12px 0 0 0;">${meaning}</p>
    </div>
  `;
});

// Summarize interpretation to about 1/3 length - keep only key points
let interpretationText = reading.interpretation;
// Take just the first 2-3 paragraphs or first ~300 characters
const paragraphs = interpretationText.split('\n\n');
if (paragraphs.length > 2) {
  interpretationText = paragraphs.slice(0, 2).join('\n\n');
}

// Convert markdown to HTML (basic conversion)
let interpretationHTML = interpretationText
  .replace(/### (.*?)$/gm, '<h3 style="font-family: Georgia, serif; color: #fbbf24; font-size: 20px; margin: 20px 0 10px 0;">$1</h3>')
  .replace(/## (.*?)$/gm, '<h2 style="font-family: Georgia, serif; color: #fde047; font-size: 24px; margin: 25px 0 12px 0;">$1</h2>')
  .replace(/# (.*?)$/gm, '<h1 style="font-family: Georgia, serif; color: #fef3c7; font-size: 28px; margin: 30px 0 15px 0;">$1</h1>')
  .replace(/\*\*(.*?)\*\*/g, '<strong style="color: #fef3c7; font-weight: bold;">$1</strong>')
  .replace(/\*(.*?)\*/g, '<em style="color: #fbbf24; font-style: italic;">$1</em>')
  .replace(/^- (.*?)$/gm, '<li style="margin: 8px 0; line-height: 1.7;">$1</li>')
  .replace(/\n\n/g, '</p><p style="font-family: Georgia, serif; color: #e7e5e4; line-height: 1.8; margin: 15px 0;">');

// Wrap list items in ul tags
if (interpretationHTML.includes('<li')) {
  interpretationHTML = interpretationHTML.replace(/(<li.*?<\/li>)/gs, '<ul style="font-family: Georgia, serif; color: #e7e5e4; margin: 15px 0; padding-left: 25px;">$1</ul>');
}

// Wrap in paragraph tags if not already wrapped
if (!interpretationHTML.startsWith('<')) {
  interpretationHTML = `<p style="font-family: Georgia, serif; color: #e7e5e4; line-height: 1.8; margin: 15px 0;">${interpretationHTML}</p>`;
}

// Build complete HTML email
const emailHTML = `
<!DOCTYPE html>
<html lang="en-GB">
<head>
  <meta charset="UTF-8">
  <meta name="viewport" content="width=device-width, initial-scale=1.0">
  <title>Daily Tarot Reading</title>
</head>
<body style="margin: 0; padding: 0; background: linear-gradient(135deg, #292524 0%, #1c1917 50%, #292524 100%); font-family: Georgia, serif;">
  <div style="max-width: 680px; margin: 0 auto; padding: 40px 20px;">

    <!-- Header -->
    <div style="text-align: center; margin-bottom: 40px;">
      <h1 style="font-family: 'Georgia', serif; color: #d4af37; font-size: 42px; margin: 0 0 10px 0; text-shadow: 0 2px 10px rgba(212, 175, 55, 0.3);">üåô S√çDHE üåô</h1>
      <div style="width: 200px; height: 2px; margin: 15px auto; background: linear-gradient(to right, transparent, #d4af37, transparent);"></div>
      <h2 style="font-family: 'Georgia', serif; color: #fbbf24; font-size: 24px; margin: 15px 0 5px 0; font-weight: normal;">Your Daily Tarot Reading</h2>
      <p style="font-family: 'Georgia', serif; color: #a8a29e; font-size: 16px; font-style: italic; margin: 5px 0 0 0;">${formattedDate}</p>
    </div>

    <!-- Spread Type -->
    <div style="text-align: center; margin-bottom: 35px;">
      <div style="display: inline-block; padding: 12px 30px; background: rgba(212, 175, 55, 0.15); border: 2px solid #d4af37; border-radius: 6px;">
        <p style="font-family: 'Georgia', serif; color: #fbbf24; font-size: 16px; margin: 0; font-weight: 600;">Three Card Spread: Past ‚Ä¢ Present ‚Ä¢ Future</p>
      </div>
    </div>

    <!-- Cards Section -->
    <div style="margin-bottom: 45px;">
      <h2 style="font-family: 'Georgia', serif; color: #d4af37; font-size: 28px; margin: 0 0 25px 0; text-align: center;">‚ú® The Cards Drawn ‚ú®</h2>
      ${cardsHTML}
    </div>

    <!-- Interpretation Section -->
    <div style="margin-bottom: 40px; padding: 30px; background: linear-gradient(135deg, #1c1917 0%, #292524 100%); border: 2px solid #cd7f32; border-radius: 8px;">
      <h2 style="font-family: 'Georgia', serif; color: #d4af37; font-size: 28px; margin: 0 0 20px 0; text-align: center;">üîÆ The Interpretation üîÆ</h2>
      <div style="font-family: 'Georgia', serif; color: #e7e5e4; font-size: 16px; line-height: 1.8;">
        ${interpretationHTML}
      </div>
    </div>

    <!-- Celtic Mythology Note -->
    ${reading.meaningType === 'celtic' ? `
    <div style="text-align: center; padding: 20px; background: rgba(16, 185, 129, 0.1); border: 1px solid rgba(16, 185, 129, 0.3); border-radius: 6px; margin-bottom: 30px;">
      <p style="font-family: 'Georgia', serif; color: #6ee7b7; font-size: 14px; margin: 0; font-style: italic;">‚ú¶ This reading incorporates Celtic mythology and symbolism ‚ú¶</p>
    </div>
    ` : ''}

    <!-- View Full Reading Button -->
    ${reading.shareableUrl ? `
    <div style="text-align: center; margin-bottom: 30px;">
      <a href="${reading.shareableUrl}" style="display: inline-block; padding: 14px 32px; background: #d4af37; color: #1c1917; text-decoration: none; border-radius: 6px; font-family: 'Georgia', serif; font-weight: bold; font-size: 16px; box-shadow: 0 4px 12px rgba(212, 175, 55, 0.3);">View Full Reading Online</a>
    </div>
    ` : ''}

    <!-- Footer -->
    <div style="text-align: center; padding-top: 30px; border-top: 1px solid rgba(212, 175, 55, 0.3);">
      <p style="font-family: 'Georgia', serif; color: #78716c; font-size: 13px; font-style: italic; margin: 10px 0;">"The threads of fate are woven through the seasons of the soul"</p>
      <p style="font-family: 'Georgia', serif; color: #57534e; font-size: 12px; margin: 15px 0 0 0;">Generated by S√çDHE Celtic Tarot ‚Ä¢ Powered by Claude Sonnet 4.5</p>
    </div>

  </div>
</body>
</html>
`;

return {
  subject: `üåô Your Daily Tarot Reading - ${formattedDate}`,
  html: emailHTML,
  text: `Your Daily Tarot Reading - ${formattedDate}\n\n${reading.cards.map(c => `${c.position}: ${c.name} ${c.isReversed ? '(Inverted)' : ''}\nKeywords: ${c.keywords.join(', ')}\n${c.isReversed ? c.reversed_meaning : c.upright_meaning}\n`).join('\n')}\n\nInterpretation:\n${reading.interpretation}`
};
